import connectDb from "../../utils/connectDb";
import axios from "axios";

connectDb();

export default async (req, res) => {

    let { location, interest } = req.body;
    const recomendedplaces = [];

    interest = "relaxation";
    location = "ss world park";

    // console.log(location);
    // console.log(interest);


    try {
        const url = `http://localhost:5000/recommand`;
        const payload = { location, interest };
        const response = await axios.post(url, payload);
        recomendedplaces.push(response.data);
    } catch (error) {
        console.error(error);
        res.status(500).send("Error . Please try again later");
    }


    console.log(recomendedplaces);

    const places = [];
    for(var x = 0; x < recomendedplaces.length; x++){
        console.log(recomendedplaces.places[x]);
    }

   

    //console.log(places);
    


};
