import connectDb from "../../utils/connectDb";
import axios from "axios";
import Plcaeinfo from  '../../models/Placeinfo';

connectDb();

 async function GetRecomendedPlace(location, interest) {

  
    let recomendedplaces;

    interest = "relaxation";
    location = "ss world park";

    console.log(location);
    console.log(interest);


    try {
        const url = `http://localhost:5000/recommand`;
        const payload = { location, interest };
        const response = await axios.post(url, payload);
        recomendedplaces = response.data.places;
    } catch (error) {
        console.error(error);
        res.status(500).send("Error . Please try again later");
    }
    // console.log(recomendedplaces);
    const places = [];
    for(var x = 0; x < recomendedplaces.length; x++){
        places.push(recomendedplaces[x].split(",")[0]);
    }

    return places;
};


async function GeneratePlace(req, res) {

    const { places } = req.body;

    const placesdata = [];

    try {

        for(var x = 0; x < places.length; x++){
            const data = await Plcaeinfo.find({ name: places[x] });
            placesdata.push(data);
        }
        // console.log(placesdata);
        res.status(201).json(placesdata);
      } catch (error) {
        console.error(error);
        res.status(500).send("Error . Please try again later");
      }


};


module.exports.GetRecomendedPlace = GetRecomendedPlace;
module.exports.GeneratePlace = GeneratePlace;
